inue; // ASCII字符        int num_bytes;        if      ((byte & 0xE0) == 0xC0) num_bytes = 2;        else if ((byte & 0xF0) == 0xE0) num_bytes = 3;        else if ((byte & 0xF8) == 0xF0) num_bytes = 4;        else return false; // 无效首字节        for (int i = 1; i < num_bytes; ++i) {            c = fgetc(file);            if (c == EOF || (c & 0xC0) != 0x80) return false;        }    }    return true;}bool is_ascii(FILE *file) {    rewind(file);    int c;    while ((c = fgetc(file)) != EOF)        if (c > 0x7F) return false;    return true;}Encoding detect_encoding(const char *filename) {    FILE *file = fopen(filename, \\"rb\\");    if (!file) return ENCODING_UNKNOWN;    unsigned char bom[4] = {0};    size_t read = fread(bom, 1, 4, file);    // 检查UTF-8 BOM    if (read >= 3 && bom[0] == 0xEF && bom[1] == 0xBB && bom[2] == 0xBF) {        fclose(file);        return ENCODING_UTF8;    }    // 检查UTF-16 BE BOM    else if (read >= 2 && bom[0] == 0xFE && bom[1] == 0xFF) {        fclose(file);        return ENCODING_UTF16BE;    }    // 检查UTF-16 LE BOM    else if (read >= 2 && bom[0] == 0xFF && bom[1] == 0xFE) {        fclose(file);        return ENCODING_UTF16LE;    }    fseek(file, 0, SEEK_SET); // 重置文件指针    // 检查UTF-8结构    if (is_valid_utf8(file)) {        fclose(file);        return ENCODING_UTF8;    }    // 检查ASCII    rewind(file);    if (is_ascii(file)) {        fclose(file);        return ENCODING_ASCII;    }    fclose(file);    return ENCODING_ANSI;}int main(int argc, char *argv[]) {    if (argc < 2) {        printf(\\"Usage: %s <filename>n\\", argv[0]);        return 1;    }    Encoding enc = detect_encoding(argv[1]);    switch (enc) {        case ENCODING_ASCII:            printf(\\"ASCIIn\\");            break;        case ENCODING_UTF8:            printf(\\"UTF-8 (无BOM)n\\");            break;        case ENCODING_UTF16LE:            printf(\\"UTF-16 LEn\\");            break;        case ENCODING_UTF16BE:            printf(\\"UTF-16 BEn\\");            break;        